import React, {useState, useEffect, Component} from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Switch from '@material-ui/core/Switch';

//Card
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';



class App extends Component{
  constructor(props){
    super(props);
    this.state={
      toggleSpan: true,
      servings:100,
      lollipop:100,
      iceCandy:200,
      savings:100
    }
  }

  // const [state, setState] = React.useState({
  //   toggleSpan: true,
  //   servings:100,toggleSpan
  //   lollipop:100,
  //   iceCandy:200,
  //   savings:100
  // });

  // const [toggleSpan,setToggleSpan]=useState(true);
  // const [servings,setServings]=useState(100);
  // const [lollipop,setLollipop]=useState(100);
  // const [iceCandy,setIceCany]=useState(200);
  // const [savings,setSavings]=useState(100);

  handletoggleSpan = (event) => {
    // console.log("ev",event.target.checked)
    // this.setState({toggleSpan:!this.state.toggleSpan});
    // setToggleSpan(event.target.checked)
    console.log(this.state.savings)
    console.log(this.state.toggleSpan)
    if(event.target.checked){
      this.setState({lollipop:this.state.servings*1,iceCandy:this.state.servings*2,toggleSpan:!this.state.toggleSpan,savings:(this.state.servings*1)-(this.state.servings*2)});
      // setIceCany(servings*2);
      console.log(this.state.lollipop,"=",this.state.iceCandy)    
    }else{      
      this.setState({lollipop:this.state.servings*0.5,iceCandy:this.state.servings*1,toggleSpan:!this.state.toggleSpan,savings:(this.state.servings*0.5)-(this.state.servings*1)});
      console.log(this.state.lollipop,"=",this.state.iceCandy)
    }
    // setTimeout(() => {
    //   setSavings(iceCandy-lollipop)
    // }, 200);
    // this.temp;
    // useEffect(()=>{      

    //   setSavings(iceCandy-lollipop)
    // },[savings])  
    // console.log(savings)
    // useEffect(()=>{
      console.log(this.state.toggleSpan,"-",this.state.servings,"-",this.state.savings,"-",this.state.lollipop,"-",this.state.iceCandy)
    // })
  };

  componentDidMount(){
  }


  handleServings=(event)=>{
    console.log(event)
    // setState({ ...state, servings: event });
  }

  render(){
  return (
    <div className='App'>
      <div className="first-vertical">
        <div className="first-half">
          <p className="looking-for-a-lollip">Looking for a lollipop alternative?</p>
          <p className="body">Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s.</p>
          <div style={{float:'left'}}>
            <button className="bg cta-text">Primary CTA</button>{' '}
            <button className="outline second-cta-text">Secondary CTA</button>{' '}
          </div>
        </div>
        <div className="second-half">
          <img src={require('./assets/images/globe.png')}></img>
        </div>      
      </div>
      <div className="second-vertical">
        <img className="central-img" src={require('./assets/images/BG/png/BG.png')}></img>
        <p className="this-is-a-heading-in">This is a heading in <br></br>two lines</p>
        <img className="overlay-img" src={require('./assets/images/Group 6/png/Group_6.png')}></img>
      </div>
      <div className="third-vertical">
        <p className="compare-pricing-toda">Compare pricing today!</p>
        <p className="lorem-ipsum-is-simpl">Lorem Ipsum is simply dummy text of the printing and <strong style={{color:'#0099FF'}}>typesetting.</strong></p>
        <ButtonGroup color="primary" aria-label="outlined primary button group">
          <Button className={"btn-group","25"} name="25" component='span' onClick={this.handleServings}>25 Serving</Button>
          <Button className={"btn-group","25"} name="25" component='span' onClick={this.handleServings}>50 Serving</Button>
          <Button className={"btn-group","25"} name="25" component='span' onClick={this.handleServings}>100 Serving</Button>
          <Button className={"btn-group","25"} name="25" component='span' onClick={this.handleServings}>200 Serving</Button>
        </ButtonGroup>       

      </div>
      <div className="fourth-vertical">
        <span className="annual">Annual</span><br></br><span className="fifty-off">50% off</span>
        <Switch
          checked={this.state.toggleSpan}
          onChange={this.handletoggleSpan}
          color="primary"
          name="toggleSpan"
          inputProps={{ 'aria-label': 'primary checkbox' }}
        />
        <span className="monthly">Monthly</span>        
      </div>
      <div className="fifth-vertical">
        <Card>
          <CardContent>            
            <p className="lollipop">lollipop</p><br/>
            <span className="dolar-money">${this.state.lollipop}</span>
            <span className="per-span">Per {(this.state.toggleSpan)?'month':'annum'}</span>
            <p className="for-serving">For {this.state.servings}  serving</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent>            
            <p className="lollipop">Icecandy</p><br/>
            <span className="dolar-money">${this.state.iceCandy}</span>
            <span className="per-span">Per {(this.state.toggleSpan)?'month':'annum'}</span>
            <p className="for-serving">For {this.state.servings}  serving</p>
          </CardContent>
        </Card>
        <span className="savings-card">
        <Card >
          <CardContent>            
            <p className="lollipop">Your savings
              <br/>with lollipop</p><br/>
              <span className="dolar-money" style={{color:'#0099FF'}}>${this.state.savings}</span>
            <p className="per-span" style={{marginTop:0}}>Per {(this.state.toggleSpan)?'month':'annum'}</p>
          </CardContent>
        </Card>
        </span>
      </div>
    </div>    
  );
  }
}

export default App;
